import React, { Component } from 'react'
import { useContext } from 'react';
import { Redirect } from 'react-router-dom'
import { Link } from 'react-router-dom'
import Footer from "../Components/footer"
import { useState, useEffect } from 'react';
import '../Components/question.css'
import '../Components/creatorcss.css'
import { UserContext } from "../providers/UserProvider";
import firebase from "firebase/app";
import { auth } from "../firebase"
require('firebase/auth');
require('firebase/database');
require('firebase/storage');

const storage = firebase.storage();

const timeStamp = Date.now()
class Creator extends Component {


  static contextType = UserContext;
  constructor(props) {
    super(props)
    this.state = {
      quiz: '',
      question: '',
      image: null,
      url: "",
      progress: 0,
      uploaded: false,
      public: true
    }

  }

  handleQuestionChange = (event) => {
    this.setState({
      question: event.target.value
    });

  }
  handleQuestionImageChange = e => {
    if (e.target.files[0]) {
      const image = e.target.files[0];
      this.setState(() => ({ image }));
    }
  };

  handleQuestionImageUpload = () => {
    const { image } = this.state;
    const uploadTask = storage.ref(`/images/${timeStamp}`).put(image);
    uploadTask.on(
      "state_changed",
      snapshot => {
        // progress function ...
        const progress = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        this.setState({ progress });
      },
      error => {
        // Error function ...
        console.log(error);
      },
      () => {
        // complete function ...
        uploadTask.snapshot.ref.getDownloadURL().then((downloadURL) => {
          console.log('File available at', downloadURL);
          this.setState({ url: downloadURL });
        });
      }
    );
  };
  handleSubmit = (event) => {
    let quiz = ""
    quiz = this.state.quiz
    let question = ""
    question = this.state.question
    let url = ""
    url = this.state.url

    event.preventDefault();
    auth.onAuthStateChanged(userAuth => {

      let refer = firebase.database().ref('/users/' + userAuth.uid + '/public/' + this.state.quiz + '/')
      if (!this.state.public) {
        refer = firebase.database().ref('/users/' + userAuth.uid + '/private/' + this.state.quiz + '/')
      }
      refer.once("value").then(function (snapshot) {
        console.log(snapshot.numChildren());
        refer.child('question' + (snapshot.numChildren() + 1)).update({
          quiz: quiz,
          question: question,
          questionImageURL: url,
          by: userAuth.displayName,
          contactMail: userAuth.email,
          timeStamp: timeStamp
        })
      }).then(() => {

        this.setState({ uploaded: true });
        //console.log(error)
      });

    });
  }
  handleQuizChange = (event) => {
    let vs = ""
    vs = event.target.value
    vs = vs.trim();
    vs = vs.replace(/[^a-zA-Z ]/g, "")
    this.setState({
      quiz: vs
    });
  }

  handlePublicAssignment = (event) => {
    if (this.state.public) {
      this.setState({
        public: false
      });
    } else {
      this.setState({
        public: true
      });
    }
  }
  handlepopup = () => {
    this.setState({
      uploaded: false
    });
  }

  render() {
    return (
      <>
        <section className=" mx-4 mt-5 pt-5 ">
          <form className="whitefont" onSubmit={this.handleSubmit}>
            <div>EnterAssignmentName [Case Sensitive] ::</div>
            <input value={this.state.quiz} onChange={this.handleQuizChange} />
            <div>EnterQuestion ::</div>
            <textarea value={this.state.question} onChange={this.handleQuestionChange} />

            
            <div>Option 1 ::</div>
            <textarea value={this.state.optionOne} onChange={this.handleoptionOneChange} />

            
            <div>Option 2 ::</div>
            <textarea value={this.state.optionTwo} onChange={this.handleoptionTwoChange} />

            
            <div>Option 3 ::</div>
            <textarea value={this.state.optionThree} onChange={this.handleChange} />

            
            <div>Option 4 ::</div>
            <textarea value={this.state.optionFour} onChange={this.handleChange} />

            

            <section>

              <div className="">

                <div className="file-field input-field">
                  <div className="btn">
                    <span>File</span>
                    <input type="file" onChange={this.handleQuestionImageChange} />
                  </div>
                </div>
                <div className="row">
                  <progress value={this.state.progress} max="100" className="progress my-4" />
                </div>
                <button
                  onClick={this.handleQuestionImageUpload} className="btn effect01">
                  Upload
                  </button>
                <img src={this.state.url} alt="Uploaded Images" />
              </div>
            </section>






            <input type="checkbox" defaultChecked={this.state.public} onChange={this.handlePublicAssignment} />
            <label>Make this as Public</label>
            <button type="submit" class="btn effect01 ">Send</button>
            <Link to={this.state.uploaded}></Link>
          </form>
        </section>
        {this.state.uploaded ? <section class="details-modal"  >
          <div class="details-modal-title">
            <h1>My details modal</h1>
          </div>
          <div class="details-modal-content">
            <p>
              You can click the X in the corner or click the overlay to close this modal.
              Something like this could be useful as a nice way to show additional information,
              but that's about as far as I would take it. It's just a nice way of styling the details element.
      </p>
          </div>

          <button class="btn effect01 " onClick={this.handlepopup}>Okay</button>
        </section> : null}
      </>
    )
  }


}
export default Creator
