import React, { Component } from 'react'
import { useContext } from 'react';
import { Redirect } from 'react-router-dom'
import { Link } from 'react-router-dom'
import { RadioGroup, Radio } from 'react-radio-group'
import Footer from "../Components/footer"
import '../Components/question.css'
import { UserContext } from "../providers/UserProvider";
import firebase from "firebase/app";
import { auth } from "../firebase"
require('firebase/auth');
require('firebase/database');

class Question extends Component {


    constructor(props) {
        super(props);
        this.state = {

            username: '',
            rendernow: false,
            questions: [],
            numberofquestions: 0,
            cquestion: 0,
            selectedValue: '0',
            value: "",
            corrects:0,
        }
    }


    componentDidMount() {

        let username = ""

        auth.onAuthStateChanged(user => {



            username = user.uid
            this.setState({ username: username })
            let questionref = firebase.database().ref('/quizes/' + this.props.match.params.id + '/q/')
            questionref.on("value", snap => {
                let questions = []
                let numberofquestions = 0
                snap.forEach(data => {

                    questions.push(data.val())
                    this.setState({ questions: questions })

                    numberofquestions++
                    this.setState({ numberofquestions: numberofquestions })

                    console.log(data.val())
                    this.setState({ rendernow: true })

                })

            })
        })
    }

    nextQuestion() {

            if(this.state.questions[this.state.cquestion].answer==this.state.selectedValue){
                    let corrects =(this.state.corrects) +1
                    this.setState({corrects :corrects})
                    console.log(this)
                    console.log(this.state.questions[this.state.cquestion].answer+"  noted  "+this.state.selectedValue)
            }
        if (this.state.cquestion < this.state.numberofquestions - 1) {
            let cquestion = this.state.cquestion
            cquestion = cquestion + 1;
            this.setState({ cquestion: cquestion })
            this.setState({selectedValue :'0'})
            
            console.log(this.state.selectedValue)
            console.log("ww" + this.state.cquestion + "  " + this.state.numberofquestions)
        } else { this.setState({ finished: true })
    
        this.setState({selectedValue :'0'}) 
        let userref =firebase.database().ref('/users/'+this.state.username+'/attempted/')
        userref.child('/'+this.props.match.params.id+'/').update({
            quizName :this.props.match.params.id,
            corrects :this.setState.corrects,
            maxcorrects :this.state.numberofquestions,
        }).then(()=>{
            alert("updated")
        })
    
    }
    }

    render() {

        if (this.state.rendernow) {
            if (this.state.username != '') {
                return (
                    <>
                        <section className=" mt-5 mx-5  ">
                            <div className="row justify-content-center">
                                <div className="whiteFont italic">

                                    <h1>Question FOR {this.props.match.params.id}</h1>
                                </div>
                            </div>

                            <div className="glass p-4 my-4">
                                <h3>Pattern of Exam</h3>
                                <div className="px-5">
                                    <li>by {this.state.by} and views is {this.state.views} </li>
                                </div>
                            </div>
                            <div className="glass p-4 my-4">
                                <h3>Procedure of Exams</h3>
                                <div className="px-5">


                                    <div>
                                        <h3>Question is {this.state.questions[this.state.cquestion].question}</h3>

                                        <img src={this.state.questions[this.state.cquestion].questionImageURL} />
                                        <div>{this.state.questions[this.state.cquestion].questionImageURL} </div>
                                        <div >
                                            <RadioGroup name="option" selectedValue={this.state.selectedValue} onChange={(e) => { this.setState({ selectedValue: e }) }}>
                                                <label><Radio value="1" />{this.state.questions[this.state.cquestion].optionOne}</label>
                                                <label><Radio value="2" />{this.state.questions[this.state.cquestion].optionTwo}</label>
                                                <label><Radio value="3" />{this.state.questions[this.state.cquestion].optionThree}</label>
                                                <label><Radio value="4" />{this.state.questions[this.state.cquestion].optionFour}</label>
                                            </RadioGroup>
                                        </div>
                                    </div>



                                </div>
                            </div>





                            <h6 style={{ color: 'whitesmoke' }}>By Clicking button below you are agreeing to our above rules.</h6>
                            <div className="my-5 py-5 row justify-content-center">

                                {this.state.finished ?
                                    <Link to={`/Question/${this.props.match.params.id}`} className=" btn effect01 " ><span>Finish {this.props.match.params.id}</span></Link>
                                    : <Link className=" btn effect01 " onClick={() => { this.nextQuestion() }} ><span>Next Question in {this.props.match.params.id}</span></Link>
                                }
                            </div>























                        </section>



                        <Footer />
                    </>
                )
            }
            else {

                return <Redirect to="/LoginCSI" />
            }
        } else {
            return (<>
                <h1>Instruction</h1>
            </>)
        }


    }




}
export default Question


