import React,{Component} from 'react'
import { useContext } from 'react';
import { Redirect } from 'react-router-dom'
import { Link } from 'react-router-dom'
import Footer from "../Components/footer"
import  { useState, useEffect } from 'react';
import '../Components/question.css'
import '../Components/creatorcss.css'
import { UserContext } from "../providers/UserProvider";
import firebase from "firebase/app";
import { auth } from "../firebase"
require('firebase/auth');
require('firebase/database');
require('firebase/storage');

const storage = firebase.storage();
class Creator extends Component{


    static contextType = UserContext;
    constructor(props){
        super(props)
        this.state={
            quiz:'',
            question:'',
            image:null,
            url: "",
            progress: 0,
            uploaded:false,
            public:true
        }
    }

    handleQuestionChange=(event)=>{
        this.setState({
            question:event.target.value
        });

    }
    handleQuestionImageChange = e => {
        if (e.target.files[0]) {
          const image = e.target.files[0];
          this.setState(() => ({ image }));
        }
      };

      handleQuestionImageUpload = () => {
        const { image } = this.state;
        const uploadTask = storage.ref(`/images/${Date.now()}`).put(image);
        uploadTask.on(
          "state_changed",
          snapshot => {
            // progress function ...
            const progress = Math.round(
              (snapshot.bytesTransferred / snapshot.totalBytes) * 100
            );
            this.setState({ progress });
          },
          error => {
            // Error function ...
            console.log(error);
          },
          () => {
            // complete function ...
            storage
              .ref("images")
              .child(image.name)
              .getDownloadURL()
              .then(url => {
                this.setState({ url });
              });
          }
        );
      };
      handleSubmit=()=>{
        
        event.preventDefault();
        auth.onAuthStateChanged(userAuth => {
          let refer=firebase.database().ref('/users/'+userAuth.uid+'/public/'+this.state.quiz+'/')
        if(!this.state.public){
          refer=firebase.database().ref('/users/'+userAuth.uid+'/private/'+this.state.quiz+'/')
        }
        refer.once("value")
        .then(function(snapshot) {
         console.log(snapshot.numChildren()); 
        //  refer.update({
        //   quiz:this.state.quiz,
        //   question : this.state.question,
        //   questionImageURL : this.state.url,
        //   })
          });
        });
      }
      handleQuizChange=(event)=>{
        let vs=""
        vs=event.target.value
        vs=vs.trim();
        vs=vs.replace(/[^a-zA-Z ]/g, "")
        this.setState({
          quiz:vs
      });
      }
      
      handlePublicAssignment=(event)=>{
        if(this.state.public){
          this.setState({
            public:false
        });
        }else{
          this.setState({
            public:true
        });
        }
      }

    render(){
        return(
            <>
            <section className= " mx-4 mt-5 pt-5 ">
                <form className="whitefont">
                    <div>EnterAssignmentName [Case Sensitive] ::</div>
                    <input value={this.state.quiz} onChange={this.handleQuizChange}/>
                    <div>EnterQuestion ::</div>
                    <textarea value={this.state.question} onChange={this.handleQuestionChange}/>
                
                    <input type="checkbox" defaultChecked={this.state.public} onChange={this.handlePublicAssignment} />
                    <label>Make this as Public</label>
                    <button type="submit" class="btn effect01 ">Send</button>
                        <Link to={this.state.uploaded}></Link>
                </form>
            </section>



            <section>
                
      <div className="center">
          <br/>
          <h2 className="green-text">React Firebase Image Uploader</h2>
          <br/>
          <br/>
        <div className="row">
          <progress value={this.state.progress} max="100" className="progress" />
        </div>
        <br />
        <br />
        <br />
        <div className="file-field input-field">
          <div className="btn">
            <span>File</span>
            <input type="file" onChange={this.handleQuestionImageChange} />
          </div>
          <div className="file-path-wrapper">
            <input className="file-path validate" type="text" />
          </div>
        </div>
        <button
          onClick={this.handleQuestionImageUpload}
          className="waves-effect waves-light btn"
        >
          Upload
        </button>
        <br />
        <br />
        <img
          src={this.state.url || "https://via.placeholder.com/400x300"}
          alt="Uploaded Images"
          height="300"
          width="400"
        />
      </div>
            </section>
            </>
        )
    }


}
export default Creator
