                    this.setState({ studentslist: privatequizlist });
                  });
                
        }

        render(){
            return(
                <>
                <div >

                <section className=" mx-4 mt-5 pt-5 ">
                    <div className="row justify-content-center">
                         {this.state.quizlist.map(data =>{
                                    <div>{data.question1}</div>

