import React, { Component } from 'react'
import { useContext } from 'react';
import { Redirect } from 'react-router-dom'
import { Link } from 'react-router-dom'
import Footer from "../Components/footer"
import { useState, useEffect } from 'react';
import '../Components/question.css'
import '../Components/creatorcss.css'
import '../Components/profilecss.css'
import { UserContext } from "../providers/UserProvider";
import firebase from "firebase/app";
import { auth } from "../firebase"
require('firebase/auth');
require('firebase/database');
require('firebase/storage');


const storage = firebase.storage();
const timeStamp = Date.now()
class Modifier extends Component{
    constructor(props) {
    
        super(props);
       
        this.state = {
            publicquizlist : [],
            cquizName:'',
            showquestion : false,
            qes:[],
            auth:[],
            questionslist :[],
            showfullquestion:true,
            cquestion:'',

        }
        }


        componentDidMount(){
            


            auth.onAuthStateChanged(userAuth => {

           let refer = firebase.database().ref('/users/' + userAuth.uid + '/private/')
                        let uid=userAuth.uid
                        this.setState({auth : uid})
                refer.on("value", snapshot => {
                    let publicquizlist = [];
                    snapshot.forEach(snap => {
                    // console.log(snap.val())
                    // console.log(snapshot)
                        publicquizlist.push(snap.val());
                    });
                    this.setState({ publicquizlist: publicquizlist });
                  });
        })}

         handleQuizChoose=(quizName)=>{
             
            this.setState({cquizName :quizName})

            console.log(this.state.cquizName)
            console.log(this.state.auth)
            let questions = firebase.database().ref('/users/' + this.state.auth + '/private/'+quizName+'/q')
            questions.on("value", snapshot => {
                let questionslist =[];
                snapshot.forEach(snap => {
                        
                     //console.log(snap.val())
                     
                    questionslist.push(snap.val());
                    
                    });
                    this.setState({questionslist :questionslist})
                    
            })
            this.setState({showquestion :true})
        }
        handleQuestionChoose=(q)=>{

            this.setState({cquestion :q.question})
            this.setState({optionOne :q.optionOne})
            this.setState({ optionTwo  : q.optionTwo  })
            this.setState({  optionThree : q. optionThree })
            this.setState({ optionFour  : q.optionFour  })
            this.setState({ url :q.questionImageURL})
                this.setState({showfullquestion :true})
        }
        handlecquestionchange=(event)=>{

            this.setState({cquestion :event.target.value})
        }
        handlecquestionOptionOnechange=(event)=>{
            this.setState({optionOne :event.target.value})
        }
        handlecquestionOptionTwochange=(event)=>{
            this.setState({optionTwo :event.target.value})
        }
        handlecquestionOptionThreechange=(event)=>{
            this.setState({optionThree :event.target.value})
        }
        handlecquestionOptionFourchange=(event)=>{
            this.setState({optionFour :event.target.value})
        }
        handleQuestionImageChange = e => {
    
            this.setState({ status: "Image Linked !" });
            if (e.target.files[0]) {
              const image = e.target.files[0];
              this.setState(() => ({ image }));
            }
          };
        
  handleQuestionImageUpload = (event) => {
    
    this.setState({ status: "preparing image to upload Image !" });
    this.setState({ progress: 30 });
    event.preventDefault();
    const { image } = this.state;
    const uploadTask = storage.ref(`/images/${timeStamp}`).put(image);
    uploadTask.on(
      "state_changed",
      snapshot => {
        // progress function ...
        const progress = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        this.setState({ progress });
      },
      error => {
        // Error function ...
        console.log(error);
      },
      () => {
        // complete function ...
        uploadTask.snapshot.ref.getDownloadURL().then((downloadURL) => {
          console.log('File available at', downloadURL);
          
        this.setState({ status: "ImageURL created !" });
          this.setState({ url: downloadURL });
          this.UploadQuestionChanges()
        });
      }
    );
  };
  UploadQuestionChanges =()=>{
   let  questionReference= firebase.database.ref("/users/"+this.state.auth+"/private/"+this)
  }

        render(){
            return(
                <>
                <div >

                <section className=" mx-4 mt-5 pt-5 ">
                    <h1>hellos</h1>
                    <div className="row justify-content-center">
                         {this.state.publicquizlist.map(data =>{ 
                             return(
                                    <div className="boxm" onClick={()=>{  this.handleQuizChoose(data.quizName)}}><h4>{data.quizName}</h4></div>
                             )

                         })}
                    </div>

                    {this.state.showquestion ?
                    <div className="row justify-content-center" >

                         {this.state.questionslist.map((data) =>{    
                                return(<div>
                                    <div className="boxm" onClick={()=>{ this.handleQuestionChoose(data) }}><h4>{data.question}</h4></div>
                                    </div>) 
                         })}
                         <div className="boxm" onClick={()=>{  }}><h4>ADD</h4></div>
                                    
                    </div> :null}
                    {this.state.showfullquestion?<section>
                            <span>Question ::</span>
                            <textarea value={this.state.cquestion} onChange={this.handlecquestionchange}></textarea>
<span>optionOne ::</span>
<textarea value={this.state.optionOne} onChange={this.handlecquestionOptionOnechange}></textarea>

<span>optionTwo ::</span>
<textarea value={this.state.optionTwo} onChange={this.handlecquestionOptionTwochange}></textarea>

<span>optionThree ::</span>
<textarea value={this.state.optionThree} onChange={this.handlecquestionOptionThreechange}></textarea>

<span>optionFour ::</span>
<textarea value={this.state.optionFour} onChange={this.handlecquestionOptionFourchange}></textarea>

<span>Image</span>
<img src={this.state.url} alt="Uploaded Images" />
<span>{this.state.url}</span>

<span>Choose Replace Image ::</span>
<input type="file" accept="image/*" onChange={this.handleQuestionImageChange} />


<button onClick={()=>{}} class="btn effect01 ">Save Changes to Question</button>


                    </section>:null}
                </section>
                </div>
                </>
            )
        }
}


export default Modifier
