import React, { Component, createContext } from "react";
import { useContext } from 'react';
import { Redirect } from 'react-router-dom'
import { Link } from 'react-router-dom'
import { UserContext } from "../providers/UserProvider";
import "../Components/home.css"
import "../Components/profilecss.css"
import { auth } from "../firebase";
import firebase from "firebase/app";
require('firebase/auth');
require('firebase/database');




const ProfilePage = () => {

  const user = useContext(UserContext);
  //const {photoURL, displayName, email} = user;

  //console.log(user)
  if (user != null) {

    
    return (
      <>
        <section className=" mt-5 ">
          <div className="row justify-content-center">

            <div className="mainSec">
              <div>
                <img src={user.photoURL}></img>
              </div>
            </div>

          </div>
          <div className=" mx-5 mt-4 pt-4  p-3 row justify-content-center barbox">
          <div className="  px-5"><strong>{user.displayName}</strong></div>
          <div className=" px-5"><strong>{user.email}</strong> </div>
          
          </div>
          <div className=" mt-5 row justify-content-center">
          <Link to='/Modifier'><div className= "p-4 boxs barbox" >Your Tests</div></Link></div>
          
          <div className="mt-4 pt-4 row justify-content-center">
            <Link to='/' className="btn effect01" onClick={() => { auth.signOut() }}><span>SignOut From CSI</span>
            </Link>


{/* 
            <Link to='/Creator'><div className="boxs" >Create Test</div></Link> */}


          </div>
          <Boxes/>


        </section>








      </>
    )




  } else {

    return <Redirect to="/" />

  }
};
export default ProfilePage;



export class Boxes extends Component {
  constructor(props) {
    super(props);
    this.state = {
        authName:'',
        attended :[],
        showattended :false,
        
    }
}

  componentDidMount(){
    let attended=[];
    auth.onAuthStateChanged(auth=>{
        this.setState({authName :auth.displayName})
        this.setState({authid :auth.uid})
        let userref = firebase.database().ref('/users/'+auth.uid+'/attempted/')
        userref.once("value").then(function (snapshot) {
          snapshot.forEach(snap =>{
              attended.push(snap.val())
              console.log(snap.val())
          })

        }).then(()=>{
          
          this.setState({showattended :true})
        })
           
    })
  }
  render(){
    return(<>

            <h1>hello</h1>
              {this.state.showattended ?<div>
                <div className=" mt-5 row justify-content-center">
                  <h1>hello</h1>
                  {this.state}
                  {this.state.attended.map(data=>{
                    console.log(data)
                    return(
                    <div>

                      <h1>{data.corrects}</h1>
                    </div>
                    
                    )
                  })}
                   </div>
              </div>:null}
           


    </>)
  }
}
